INSERT INTO sp_ecms_news ( `id`, `classid`, `ttid`, `onclick`, `plnum`, `totaldown`, `newspath`, `filename`, `userid`, `username`, `firsttitle`, `isgood`, `ispic`, `istop`, `isqf`, `ismember`, `isurl`, `truetime`, `lastdotime`, `havehtml`, `groupid`, `userfen`, `titlefont`, `titleurl`, `stb`, `fstb`, `restb`, `keyboard`, `title`, `newstime`, `titlepic`, `ftitle`, `smalltext`, `writer`, `befrom`, `newstext`, `diggtop` ) VALUES  ('65','100','0','0','0','0','2013-08-12','65','1','maluda1988','0','0','0','0','0','0','0','1376305969','1376305969','1','0','0','','http://home.idaap.org/learn/2013-08-12/65.html','1','1','1','','山东留学生回国做隆鼻整形','1376304450','','','				　　相比于国内学生，山东留学生对整形美容的态度更为开放。近日，记者了解到，寒假期间，山东各整形美容医院留学生整形人数比往年都要多。　　山东留学生胡筱筱过完年就24岁了。在英国留学的她受到英国人高鼻梁、双眼皮','','','				<p>　　相比于国内学生，山东留学生对<a target=\"_blank\" href=\"http://www.fh21.com.cn/meirong/\">整形美容</a>的态度更为开放。近日，记者了解到，寒假期间，山东各整形美容医院留学生整形人数比往年都要多。</p><p>　　山东留学生胡筱筱过完年就24岁了。在英国留学的她受到英国人高鼻梁、双眼皮的外貌的影响，便想通过整形手术，使自己和英国人看起来差不多。经过对比，胡筱筱发现国内的整形手术不但价格比国外便宜，整形技术水平也高，于是，过完年后，在父母的陪同下做了<a target=\"_blank\" href=\"http://www.fh21.com.cn/meirong/lb/\">隆鼻手术</a>。胡筱筱称，不少同学和她一样，选择回国做整形手术。</p><p>　　胡筱筱的父母表示，他们起初并不同意女儿做隆鼻手术，后来发现不少山东留学生回国做整形手术，就不再反对了。他们认为，女儿受国外思想、审美等各个方面的影响，会做隆鼻手术是正常的。</p><p>　　有关整形医生称，今年寒假期间，山东留学生回国做整形手术的，平均每个整形医院不少于10名，主要以面部的隆鼻手术、割双眼皮手术等为主。由于山东留学生对整形手术有着更加开放的观点，加上整形手术的技术不断提高，回国整形正逐渐成为山东留学生追求时尚的一种方式。</p><p>　　观念影响人的行为，山东留学生由于思想观念更为开放，对整形手术的接受度更高，山东留学生回国整形将成为一种趋势。</p>			','0');
