INSERT INTO sp_ecms_news ( `id`, `classid`, `ttid`, `onclick`, `plnum`, `totaldown`, `newspath`, `filename`, `userid`, `username`, `firsttitle`, `isgood`, `ispic`, `istop`, `isqf`, `ismember`, `isurl`, `truetime`, `lastdotime`, `havehtml`, `groupid`, `userfen`, `titlefont`, `titleurl`, `stb`, `fstb`, `restb`, `keyboard`, `title`, `newstime`, `titlepic`, `ftitle`, `smalltext`, `writer`, `befrom`, `newstext`, `diggtop` ) VALUES  ('83','100','0','0','0','0','2013-08-12','83','1','maluda1988','0','0','0','0','0','0','0','1376306690','1376306690','1','0','0','','http://home.idaap.org/learn/2013-08-12/83.html','1','1','1','','唇部整形术打造完美唇形','1376306690','','','				　　目前最常见的唇部美容整形手术有厚唇变薄、薄唇变厚、重唇成形、唇弓成形、唇珠成形等几种。厚唇及重唇需要在口腔内切除一条唇粘膜。而薄唇则是在上唇边缘切除一条皮肤后使红唇外翻变厚，同时可以形成起伏明显','','','				<p>　　目前最常见的<a target=\\\"_blank\\\" href=\\\"http://www.fh21.com.cn/meirong/wg/cb/\\\">唇部美容整形</a>手术有厚唇变薄、薄唇变厚、重唇成形、唇弓成形、唇珠成形等几种。厚唇及重唇需要在口腔内切除一条唇粘膜。而薄唇则是在上唇边缘切除一条皮肤后使红唇外翻变厚，同时可以形成起伏明显的唇弓。明显突出的唇珠需要利用局部的红唇组织进行再造。</p><p>　　优美的口唇可以展示人的端庄淳厚秀丽高雅和无限的魅力。所以口唇的美与丑必须与每个人的具体特征相符合，比例适度，均衡协调才为美。即必须与脸型相配、与五官相协调、与自己的性格气质相符合。一般说来，薄唇小口给人有一种轻巧灵活之感;厚唇大口给人一种厚朴之感。另外唇的厚度与鼻形有一定的关系，薄唇和中等厚度的唇常伴有狭鼻形，而厚唇则伴有阔鼻形。</p><p>　　唇的美美在唇形，美在与面部整体的和谐。上唇，下唇及其间的口裂共同组成完整的口唇部。唇部由皮肤部份(也称白唇)及红唇两大部分组成。口唇具有相应正常的上唇高度，与之相适应的红唇侧方凸度，红唇正面观的匀称的厚度及与面部形态和其它器官相和谐的口裂宽度，才能称为一个自然匀称的并与面部相和谐的美的口唇。</p><p>　　这种技术对设备和技术的要求相当高，求美者选择医生和医院时需慎重。如果想进行这项手术，就一定要谨慎选择手术机构，最好在正规的医院进行，以避免手术质量受到影响，导致手术效果降低或者出现各种不良的并发症，而且也可以保证得到良好的术后服务。</p>			','0');
INSERT INTO sp_ecms_news ( `id`, `classid`, `ttid`, `onclick`, `plnum`, `totaldown`, `newspath`, `filename`, `userid`, `username`, `firsttitle`, `isgood`, `ispic`, `istop`, `isqf`, `ismember`, `isurl`, `truetime`, `lastdotime`, `havehtml`, `groupid`, `userfen`, `titlefont`, `titleurl`, `stb`, `fstb`, `restb`, `keyboard`, `title`, `newstime`, `titlepic`, `ftitle`, `smalltext`, `writer`, `befrom`, `newstext`, `diggtop` ) VALUES  ('82','100','0','0','0','0','2013-08-12','82','1','maluda1988','0','0','0','0','0','0','0','1376306690','1376306690','1','0','0','','http://home.idaap.org/learn/2013-08-12/82.html','1','1','1','','唇美有道厚唇重唇皆需和谐','1376306690','','','				　　有人要丰唇，有人却要治疗自己的厚唇。美是协调，面部的美在于五官间的和谐。有些人的五官细致、秀气，配上薄唇会与人如沐春风的感觉。反之，长了厚唇，则会给人别扭、蠢钝的印象。求美不是追求流行，而是选择最适合自己','','','				<p>　　有人要<a target=\\\"_blank\\\" href=\\\"http://www.fh21.com.cn/meirong/chunbu/fcs/\\\">丰唇</a>，有人却要治疗自己的厚唇。美是协调，面部的美在于五官间的和谐。有些人的五官细致、秀气，配上薄唇会与人如沐春风的感觉。反之，长了厚唇，则会给人别扭、蠢钝的印象。求美不是追求流行，而是选择最适合自己的五官。因此，厚唇于某些人也是需要治疗的。</p><p>　　所谓&ldquo;厚唇&rdquo;是指，男性唇厚度上唇超过9毫米，下唇超过 10.5毫米;女性上唇超过8毫米，下唇超过9毫米。厚唇从审美的角度来看，总是给人一种&ldquo;愚钝&rdquo;的感觉。</p><p>　　重唇又称双唇或双上唇，重唇主要见于上唇，多在青春期表现最为明显，质地均与正常无异，少数病人可能有家族史。该畸形对容貌影响很大，在闭口时，畸形不显，开口时，可见两唇缘。在两唇缘间有一横沟，笑时呈现两道清楚的红唇。</p><p>　　厚唇和重唇会严重影响人的面部观感。那些对自己五官要求很高的求美者，可以通过医疗整形手段，来治疗厚唇和重唇。如果您已经决定进行唇部的矫正治疗，请一定选择具有医疗美容资质的正规医院，进行咨询和治疗。以免给您的身心带来不必要的伤害。</p>			','0');
