INSERT INTO sp_posts ( `id`, `post_author`, `post_keywords`, `post_date`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `post_modified`, `post_content_filtered`, `post_parent`, `post_type`, `post_mime_type`, `comment_count`, `smeta`, `post_hits`, `post_like`, `istop`, `recommended` ) VALUES  ('7','1','入会','2015-01-07 15:54:13','<p><br/></p><p style=\"text-align:center\"><img alt=\"入会申请.jpg\" src=\"http://thinkphp.besteee.com/data/upload/ueditor/2015010703533854ace60246648.jpg\" height=\"301\" width=\"322\"/></p><h2>&nbsp;</h2><h2>会员管理办法&nbsp;</h2><p style=\"text-align: left\"><br/>&nbsp;&nbsp;&nbsp; 为规范香港国际美容医师行业协会（以下简称协会）会员的产生、退出，明确会员的权利义务，加强会员日常管理，维持协会的协调有效运转，根据《香港国际美容整形医师行业协会章程（草案）》制定本办法。<br/>&nbsp;<br/>壹、会员资格<br/>热心于整形美容事业、信守协会章程、自愿申请并具备下述条件之壹的机构或个人均可成为本协会会员。<br/>（壹）在临床医学领域开展医疗整形、医疗美容工作且具备相应执业资格的机构或个人；<br/>（二）卫生行政部门或开展整形美容科研活动的机构及个人；<br/>（叁）热心于医疗整形、医疗美容事业的相关机构及个人。<br/>二、会员管理<br/>（壹）会员的产生<br/>1.向理事会或理事会授权机构提交入会申请书；<br/>2.经理事会或理事会授权机构讨论通过；<br/>3.由理事会或理事会授权机构发给会员证。<br/>（二）会员的退出<br/>1.履行完在会期间应承担的会员义务；<br/>2.向理事会或理事会授权机构提交退会申请书；<br/>3.经理事会或理事会授权机构讨论通过其退会申请；<br/>4.向理事会或理事会授权机构上交会员证；<br/>5.退会后，不得继续以本协会会员名义从事相关工作。<br/>6.会员壹年不交纳会费或不参加本协会活动的，视为自动退会。<br/>叁、会员权利<br/>会员享有下列权利：<br/>（壹）本协会的选举权、被选举权和表决权，参与协会内部有关事务的管理权；<br/>（二）对协会的批评、建议和监督的权利；<br/>（叁）优先参加本协会举办的各项活动的权利；<br/>（四）获得本协会服务的优先权；<br/>（五）入会自愿、退会自由。<br/>四、会员义务<br/>会员须履行下列义务：<br/>（壹）执行本协会的决议；<br/>（二）维护本协会的合法权益；<br/>（叁）完成本协会交办的工作；<br/>（四）按规定交纳会费；<br/>（五）向本协会反映情况，提供有关资料。<br/>五、协会有关交费标准及方式<br/>（壹）个人会员会费标准<br/>类别会费标准（元/年）五年会费合计（元）<br/>个人会员60&nbsp;&nbsp;&nbsp; 300<br/>科研机构800&nbsp;&nbsp;&nbsp; 4000<br/>医疗机构1500&nbsp;&nbsp;&nbsp; 7500<br/>教育培训机构1500&nbsp;&nbsp;&nbsp; 7500<br/>企业4000&nbsp;&nbsp;&nbsp; 20000<br/>（二）会费交纳方式<br/>会员会费须五年壹次性交纳，理事、常务理事单位（个人）会费可按年交纳，也可五年壹次性交纳。交费日期为每年的6月1日-6月30日。协会收到会费并确认后，及时向交费个人或单位出具正式单据。<br/>国际美容整形医师行业协会</p><p style=\"text-align: left\">&nbsp;</p><h3>如已注册请输入账号密码，点击登录，若未注册，点击注册，立即成为会员</h3><p><br/></p>','入会相关流程','','1','1','2015-01-07 15:53:30','','0','2','','0','{\"thumb\":\"\",\"template\":\"page\"}','0','0','0','0');
